var app=angular.module("contactApp",[]);app.controller("ContactController",function(t,e){t.result="hidden",t.resultMessage,t.formData,t.submitButtonDisabled=!1,t.submitted=!1,t.submit=function(s){t.submitted=!0,t.submitButtonDisabled=!0,s.$valid?e({method:"POST",url:"contact-form.php",data:$.param(t.formData),headers:{"Content-Type":"application/x-www-form-urlencoded"}}).success(function(e){console.log(e),e.success?(t.submitButtonDisabled=!0,t.resultMessage=e.message,t.result="bg-success"):(t.submitButtonDisabled=!1,t.resultMessage=e.message,t.result="bg-danger")}):(t.submitButtonDisabled=!1,t.resultMessage=" ",t.result="bg-danger")}});