app.controller("ContactController",function(s,t){s.result="hidden",s.resultMessage,s.formData,s.submitButtonDisabled=!1,s.submitted=!1,s.submit=function(e){s.submitted=!0,s.submitButtonDisabled=!0,e.$valid?t({method:"POST",url:"contact-form.php",data:$.param(s.formData),headers:{"Content-Type":"application/x-www-form-urlencoded"}}).success(function(t){console.log(t),t.success?(s.submitButtonDisabled=!0,s.resultMessage=t.message,s.result="bg-success"):(s.submitButtonDisabled=!1,s.resultMessage=t.message,s.result="bg-danger")}):(s.submitButtonDisabled=!1,s.resultMessage=" ",s.result="bg-danger")}});